var app = angular.module('app',[]); //ng-app="blabla" burada 'app' dedigimiz kisim blabla nin oldugu kisim

app.controller("MyFirstController", function ($scope) {
  $scope.kelimeler = [
    {isim:"hope",tanim:"umut"},
    {isim:"angry",tanim:"kizgin"},
    {isim:"open",tanim:"acik"},
    {isim:"state",tanim:"bolum"}
  ];

  $scope.YeniKelime = function () {
    $scope.kelimeler.push({
      isim:$scope.isim,
      tanim:$scope.tanim
    });
  };

});

app.controller("HttpController", function ($scope,$http) {

  var url = "http://jsonplaceholder.typicode.com/users"
  $http.get(url).success(function (response) {
    $scope.users = response;
  })

  $scope.SearchUser = function () {
    $scope.userResult = $scope.users.filter(function(item) {
      return item.id == $scope.postid;
    });

    $scope.postid = "";


    if(!$scope.userResult.length){
      alert("kullanici bulunamadi !");
    };
  };
});

app.controller("ClickController", function ($scope) {
  $scope.sTab = function (number) {
    $scope.tab=number;
  }
  $scope.aTab = function (value){
      return $scope.tab == value;
  }
  $scope.icerik = [
    {head:'icerik 1',number:1},
    {head:'icerik 2',number:2},
    {head:'icerik 3',number:3}
  ]
});
